import { Component } from '@angular/core';
import { ShowTeams } from '../ShowTeams';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { ShowteamsService } from '../showteams.service';

@Component({
  selector: 'app-updateteam',
  templateUrl: './updateteam.component.html',
  styleUrls: ['./updateteam.component.scss']
})
export class UpdateteamComponent {
  tid:number;
  team:ShowTeams;
  constructor(private http:HttpClient,private router:Router,private route:ActivatedRoute,private service:ShowteamsService) {}
  ngOnInit():void {
    this.getTeams();
  }
  getTeams():void {
    this.tid=+this.route.snapshot.paramMap.get("tid");
    if(this.tid !=0) {
      this.service.getTeam(this.tid).subscribe(team=>this.team=team);

    } else {
      this.team=new ShowTeams(0,"",0);
    }
  }
  /*saveTeam():void {
    this.service.saveTeam(this.team).subscribe(team=>this.team=team);
   // this.router.navigate(["/dashboard/showteams"]);
  }*/
  submit(UpdateTeams:any) {
    console.log(this.tid);
    this.service.updateTeam(this.tid,this.team).subscribe(data=>{
    alert("Team updated succesfully");
    this.router.navigate(["/dashboard/showteams"]);
    },error=>console.error());
    //this.service.saveTeam(this.team).subscribe(team=>this.team=team);


  }

}
<div style="text-align: center;color: blue;">
    <h1>Update Teams</h1>
</div>

<div class="container">
    <form #updateteams="ngForm" (ngSubmit)="submit(updateteams)">
        <div class="form-group">
            <label>Team Id</label>
            <input #name="ngModel" [(ngModel)]="team.tid" required type="text" class="form-control" id="tid" placeholder="Enter TeamId" ngModel name="tid">
        </div>  
        <div class="form-group">
            <label>Team Name</label>
            <input #name="ngModel" [(ngModel)]="team.tname" required type="text" class="form-control" id="tname" placeholder="Enter TeamName" ngModel name="tname">
        </div>  
        <div class="form-group">
            <label>Team Budget</label>
            <input #name="ngModel" [(ngModel)]="team.tbudget" required type="text" class="form-control" id="tbudget" placeholder="Enter TeamBudget" ngModel name="tbudget">
        </div> 
        <button type="submit" class="btn btn-success">Save</button>&nbsp;
        <a routerLink="/dashboard/showteams" class="btn btn-primary" routerLinkActive="active">Back</a>


    </form>
</div>
