import { Component } from '@angular/core';
import { ShowPlayers } from '../ShowPlayers';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { ShowteamsService } from '../showteams.service';

@Component({
  selector: 'app-updateplayer',
  templateUrl: './updateplayer.component.html',
  styleUrls: ['./updateplayer.component.scss']
})
export class UpdateplayerComponent {
  pid:number;
  player:ShowPlayers;
  constructor(private http:HttpClient,private router:Router,private route:ActivatedRoute,private service:ShowteamsService) {}
  ngOnInit():void {
    this.getPlayers();
  }
  getPlayers():void {
    this.pid=+this.route.snapshot.paramMap.get("pid");
    if(this.pid !=0) {
      this.service.getPlayer(this.pid).subscribe(player=>this.player=player);

    } else {
      this.player=new ShowPlayers(0,"","","","",0,0);


    }

  }
  submit(UpdatePlayers:any) {
    console.log(this.pid);
    this.service.updatePlayer(this.pid,this.player).subscribe(data=>{
    alert("Player updated succesfully");
    this.router.navigate(["/viewallplayers"]);
    },error=>console.error());
    //this.service.saveTeam(this.team).subscribe(team=>this.team=team);


  }



}
<div style="text-align: center;color: blue;">
    <h1>Update Players</h1>
</div>
<div class="container">
    <form #updateplayersinfo="ngForm" (ngSubmit)="submit(updateplayersinfo)">
        <div class="form-group">
            <label>Player Name</label>
            <input #name="ngModel" [(ngModel)]="player.pname" required type="text" class="form-control" id="pname" placeholder="Enter PlayerName" ngModel name="pname">
        </div>
        <div class="form-group">
            <label>Player Email</label>
            <input #playeremail="ngModel" [(ngModel)]="player.pemail" required type="email" class="form-control" id="pemail" placeholder="Enter PlayerEmail" ngModel name="pemail">
        </div> 
        <div class="form-group">
            <label>Player Category</label>
            <input #name="ngModel" [(ngModel)]="player.pcategory" required type="text" class="form-control" id="pcategory" placeholder="Enter PlayerCategory" ngModel name="pcategory">
        </div>
        <div class="form-group">
            <label>Player Country</label>
            <input #name="ngModel" [(ngModel)]="player.pcountry" required type="text" class="form-control" id="pcountry" placeholder="Enter PlayerCountry" ngModel name="pcountry">
        </div> 
        <div class="form-group">
            <label>Player Age</label>
            <input #name="ngModel" [(ngModel)]="player.page" required type="text" class="form-control" id="page" placeholder="Enter PlayerAge" ngModel name="page">
        </div>  
        <div class="form-group">
            <label>Player Budget</label>
            <input #name="ngModel" [(ngModel)]="player.pbudget" required type="text" class="form-control" id="pbudget" placeholder="Enter PlayerBudget" ngModel name="pbudget">
        </div>  
        <button type="submit" class="btn btn-primary">Save</button>&nbsp;


    </form>
</div>
