import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-addteams',
  templateUrl: './addteams.component.html',
  styleUrls: ['./addteams.component.scss']
})
export class AddteamsComponent {
  msg:string;
  constructor(private route:Router,private http:HttpClient,private activatedRoute:ActivatedRoute) {}
  tname:string="";
  tbudget:string="";
  submit(AddTeam:any) {
    let bodyData= {
      "tname":this.tname,
      "tbudget":this.tbudget
    };
    this.http.post("http://localhost:8080/teams/addteams",bodyData).subscribe((resultData:any)=>
    {
    console.log(resultData);
    alert("Team added Successful");
    this.route.navigate(['/dashboard/showteams']);
    },(error)=> {
      this.msg=error.error.message;
      alert(this.msg);
    });

  }

}
<div class="main-container">
    <div style="text-align: center;color: blue;">
    <h1>Add Teams</h1>
    </div>
    <div class="container">
    <form #addteams="ngForm" (ngSubmit)="submit(addteams)">
        <div class="form-group">
            <label>Team Name</label>
            <input #name="ngModel" [(ngModel)]="tname"[ngModelOptions]="{standalone:true}" required type="text" class="form-control" id="tname" placeholder="Enter TeamName" ngModel name="tname">
        </div>  
        <div class="form-group">
            <label>Team Budget</label>
            <input #name="ngModel" [(ngModel)]="tbudget"[ngModelOptions]="{standalone:true}" required type="text" class="form-control" id="tbudget" placeholder="Enter TeamBudget" ngModel name="tbudget">
        </div> 
        <button type="submit" class="btn btn-primary">Add Team</button>&nbsp;
        <a routerLink="/dashboard" class="btn btn-primary" routerLinkActive="active">Back</a>


    </form></div>
</div>
